import { NavBar } from '../NavBar';
import React, { useEffect, useState } from "react";
import cong from "../../configuration"; 
import { getDatabase, ref, onValue } from "firebase/database";
// import BlogEntry from './BlogEntry'; // Ensure correct path
import { Card } from 'react-bootstrap';
import image from '../../assets/img/school.jpg'
import './Blog.css';


const Blog = () => {

  const [data, setData] = useState([]);

  useEffect(() => {

    const database = getDatabase(cong);
    const collectionRef = ref(database, "Blogs");

    const fetchData = () => {
      onValue(collectionRef, (snapshot) => {
        const dataItem = snapshot.val();
        if(dataItem)
        {
          const displayItem = Object.values(dataItem);
          setData(displayItem);
        }
      });
    };

    fetchData();
  }, []);

  return (
    <section>
    <NavBar/>
    <div className="blog-page">

      <Card className='image-card'>
        <Card.Img src={image} alt="No image found" className="image-card-img" />
        <Card.ImgOverlay className="image-card-overlay">
        <Card.Title className="image-card-title">Banglore Trip</Card.Title>
        </Card.ImgOverlay>
      </Card>
    </div>
    </section>
  );
};

export default Blog;
