import { NavBar } from '../NavBar';
import React, { useEffect, useState } from 'react';
// import BlogEntry from './BlogEntry'; // Ensure correct path
import './Blog.css';


const Blog = () => {
    const [fileNames, setFileNames] = useState([]);
    const [data, setData] = useState([]);

    useEffect(() => {
        // Fetch the file_names.json to get the list of files
        fetch('../../assets/blog-entries/file_names.json')
          .then(response => response.json())
          .then(fileNames => {
            setFileNames(Object.values(fileNames));
          })
          .catch(error => console.error('Error fetching file names:', error));
      }, []);

      useEffect(() => {
        if (fileNames.length > 0) {
          // Fetch each JSON file listed in file_names.json
          const fetchData = async () => {
            const promises = fileNames.map(fileName =>
              fetch(`../../assets/blog-entries/${fileName}`).then(response => response.json())
            );
            const results = await Promise.all(promises);
            setData(results);
          };
          fetchData();
        }
      }, [fileNames]);

  return (
    <section>
    <NavBar/>
    <div className="blog">
        {data.map((item, index) => (
        <ImageCard key={index} title={item.title} body={item.body} image={item.image} />
        ))}
    </div>
    </section>
  );
};

export default Blog;



const ImageCard = ({ title, image }) => {
    return (
      <div style={{ position: 'relative', display: 'inline-block', margin: '10px' }}>
        <img src={`/path/to/images/${image}`} alt={title} style={{ width: '200px', height: '200px' }} />
        <div
          style={{
            position: 'absolute',
            bottom: '0',
            background: 'rgba(0, 0, 0, 0.5)',
            color: '#fff',
            width: '100%',
            textAlign: 'center',
            opacity: '0',
            transition: 'opacity 0.3s',
          }}
        >
          {title}
        </div>
      </div>
    );
  };
